import os
import json
from screens.error import window_error

_main_folder = os.path.dirname(os.path.abspath('__main__'))


def get_main_folder():
    """ Retorna directorio de la carpeta raiz del programa.
    """
    return _main_folder


def get_avatar_folder():
    """ Retorna directorio de carpeta avatar dentro de images.
    """
    return os.path.join(get_main_folder(), "images", "avatar")


def get_config_folder():
    """ Retorna directorio de carpeta config
    """
    return os.path.join(get_main_folder(), "config")


def get_image_folder():
    """ Retorna directorio del repositorio de imagen que esta guardado en el json llamado config.
    """
    try:
        with open(get_config_json()) as json_file:
            directory = json.load(json_file)
        return os.path.join(get_main_folder(), *directory["Images"][1:])
    except FileNotFoundError:
        window_error("No se pudo acceder al config.json")


def get_memes_folder():
    """ Retorna directorio que se encuentra en el json llamado config, del repositorio donde se guardaran los memes.
    """
    try:
        with open(get_config_json()) as json_file:
            directory = json.load(json_file)
        return os.path.join(get_main_folder(), *directory["Memes"][1:])
    except FileNotFoundError:
        window_error("No se puede acceder al config.json")


def get_collage_folder():
    """ Retorna directorio que se encuntra en el json llamado config, del repositorio donde se guardaran los collages.
    """
    try:
        with open(get_config_json()) as json_file:
            directory = json.load(json_file)
        return os.path.join(get_main_folder(), *directory["Collage"][1:])
    except FileNotFoundError:
        window_error("No se pudo acceder al config.json")


def get_image_default_folder():
    """ Retorna directorio de carpeta images.
    """
    return ["images"]


def get_memes_default_folder():
    """ Retorna directorio del repositorio por defecto donde se guardaran los memes.
    """
    return ["default_dir", "memes"]


def get_collage_default_folder():
    """ Retorna directorio del repositorio por defecto donde se guardaran los collages.
    """
    return ["default_dir", "collages"]


def get_interface_folder():
    """ Retorna directorio que contiene las imagenes para la interfaz.
    """
    return os.path.join(get_main_folder(), *get_image_default_folder(), "interface")


def get_datafile_folder():
    return os.path.join(get_config_folder(), 'data_files')


def get_fonts_folder():
    if os.path.exists(os.path.join(get_config_folder(), 'fonts')):
        return os.path.join(get_config_folder(), 'fonts')
    else:
        window_error("No se encuentra la carpeta 'fonts'")


