def get_theme():
    try:
        with open(get_config_json()) as json_file:
            directory = json.load(json_file)
        return directory["Theme"]
    except FileNotFoundError:
        return 'DarkGrey2'


def valid_image():
    from screens.new_profile import save_image  # Import ciruclar
    try:
        with open(get_profile_json()) as json_file:
            profiles = json.load(json_file)
            for key in profiles.keys():
                if not os.path.exists(os.path.join(get_avatar_folder(), profiles[key]['image'])):
                    save_image(os.path.join(get_avatar_folder(), 'button_perfil.png'), os.path.join(
                        get_avatar_folder(), profiles[key]['image']), False)
    except FileNotFoundError:
        with open(get_profile_json(), 'x') as json_file:
            json.dump({}, json_file)


